mapper.select:
SELECT b.b_ca_num, b.b_ca_name
FROM big_category b
ORDER BY b.b_ca_name;

SELECT s.s_ca_num, s.s_ca_name
FROM small_category s
WHERE s.b_ca_num = #{bigCategoryNum}
ORDER BY s.s_ca_name;

mapper.update:
UPDATE doc
SET d_title = #{d_title},
	d_content = #{d_content}
WHERE d_num = #{d_num}
